package day02;

public class Print {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("개행 되는 풀력문");
		System.out.print("개행 되지 않는 출력문");
		System.out.println("1234");
		System.out.printf("printf %d", 100);
		
		//특수문자
		System.out.print("1\n2\n3\n4\n5\n");
		System.out.println("1\t2\t3\t4");
		System.out.printf("원주율: %1.2f \n",3.1412);
		System.out.println("작은따옴표: \'");
		System.out.println("큰따옴표 : \"");

	}

}
